import React, { useState } from 'react';
import { Box } from "@mui/system";
import { useStyles } from "./BodyStyle";
import { Container, Grid, Hidden } from "@material-ui/core";
import Image from "../../images/contactus.jpg";
import {
  Decorator,
  Divider,
  CardMedia,
  RenderSectionHeading,
} from "../common/Common";
import { RenderInputText } from '../common/FormCommon';

const Contact = () => {
  
    const classes = useStyles();
    const [state, setState] = useState({
        data: {
            firstname: "",
            email: "",
            messages: "",
        },
        errors: {},
    });

     const handleOnChange = ({ target }) => {
    const { data, errors } = state;

    //setting errors
    target.value.length <= 3
      ? (errors[target.name] = `${target.name} at least have 3 letters`)
      : (errors[target.name] = "");

    data[target.name] = target.value;
    setState({ data, errors });
  };
const handleSubmit = (e) => {
    e.preventDefault();
    console.log("submitted ,", state.data);
    //api call to psot data
  };
  return (
    <Box className={classes.boxAbout}>
      <Container>
        <Grid container spacing={1}>
          <Grid item sm={5}>
            <Box xsDown component={Hidden}>
              <img
                src={Image}
                alt="contact us"
                className={classes.responsiveImg}
              />
            </Box>
          </Grid>
          <Grid xs={12} sm={7} className={classes.cardMedia}>
            {RenderSectionHeading({
              smallText: "Contact Me",
              heading: "Seems to be Interesting!",
              description:
                "Got my Portfolio seen, than feel free to contact me for your future projects",
            })}
           
            
                  </Grid>
                  <form onSubmit={handleSubmit}>
                  <Grid container direction="row" justify="center" alignItems="center">
                          <Grid item xs={12} sm={10} sx={{ marginBottom: "16px" }}>
                              {RenderInputText({
                                  label:"First Name"
                              })}

                      </Grid>

                  </Grid>
                  </form>
        </Grid>
      </Container>
    </Box>
  );
};

export default Contact
