import { makeStyles } from "@material-ui/core";
import { Theme } from "../Theme";
import Image from "../../images/headerBack.jpg";


export const useStyles = makeStyles((theme) => ({
  HeaderWrapper: {
    width: "100%",
    minHeight: "90vh",
    height: "auto",
    background: `linear-gradient(to bottom right,#04303140, #00606473),url(${Image})`,
    backgroundSize: "cover",
    backgroundRepeat:"no-repeat"

  },
  HeaderContainer: {
    width: "85%",
    padding:"20px",
    minHeight: "95vh",
    height: "auto",
    justifyContent: "center",
    color: Theme.colors.white,
    display: "flex",
    flexFlow: "column wrap",
    fontFamily:"roboto"
    
  },
  HeadTitle: {
    fontSize:"4rem"
  },
  HeadDesc: {
    fontSize: "3rem",
    margin: theme.spacing(1, 0)
  },
  AppBar: {
    backgroundColor:Theme.colors.deepest
  },
  
  toolbar: {
    display: "flex",
    flexFlow: "row wrap",
    justifyContent: "space-between",
  },
  decorator: {
    
    display: "flex",
    fontWeight: "bolder",
    fontFamily: "roboto",
    position: "relative",
    marginTop: "20px",
    "&:before": {
      width: "40px",
      height: "40px",
      backgroundColor: "#0097a7",
      content: '""',
      borderRadius: "50%",
    },
    
  },
  decoratorText: {
    lineHeight: "40px",
    position: "absolute",
    left: "23px",
    top:"3px"
  },
  arrow: {
    lineHeight: "50px",
    position: "absolute",
    left: "95px",
    animationName: "upDown",
    animationDuration: "0.7s",
    animationIterationCount: "infinite",
  }
}))
