import { Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useStyles } from "../header/HeaderStyle";
import { useStyles as BodyStyle } from "../body/BodyStyle";

export const Decorator = ({ label, withIcon, Icon, styles }) => {
  const classes = useStyles();
  return (
    <Box className={classes.decorator}>
      <Typography component="span" className={classes.decoratorText}>
        {label}
      </Typography>
      {withIcon ? (
        <Typography className={classes.arrow}>{Icon}</Typography>
      ) : null}
    </Box>
  );
};

export const Divider = () => {
  const classes = BodyStyle();
  return <div className={classes.divider}></div>;
};

export const RenderSectionHeading = ({
  smallText,
  heading,
  description,
  alignCenter,
}) => {
  const classes = BodyStyle();
  return (
    <Box className={classes.sectionHeadingCont}>
      {Decorator({
        label: smallText,
        withIcon: false,
        styles: alignCenter ? { width: "100px", margin: "10px auto" } : {},
      })}
      <Typography
        variant="h4"
        align={alignCenter ? "center" : "left"}
        className={classes.sectionHeading}
      >
        {heading}
      </Typography>
      {Divider({
        style: alignCenter ? { margin: "16px auto" } : { margin: "16px 0px" },
      })}
      <Typography
        variant="body1"
        component="h6"
        align={alignCenter ? "center" : "left"}
        className={classes.sectionDesc}
      >
        {description}
      </Typography>
    </Box>
  );
};
