import {
  Box,
  Button,
  Container,
  Grid,
  Hidden,
  TextField,
} from "@material-ui/core";
import React, { useState } from "react";
import { useStyles } from "./BodyStyle";
import image from "../../images/commentPic.jpeg";
import { RenderSectionHeading, CardMedia } from "../common/Common";

//import ScrollAnimation from 'react-animate-on-scroll';

export default function Comment() {
  const classes = useStyles();
  const [name, setName] = useState("");
  const [mail, setMail] = useState("");
  const [comment, setComment] = useState("");

  const handleChange = (event) => {
    setName(event.target.value);
    setMail(event.target.value);
    setComment(event.target.value);
  };

  return (
    <Box className={classes.section3} id="Comments">
      {/* <ScrollAnimation animateIn='fadeIn'> */}
      <Container>
        <Grid container spacing={1}>
          <Grid item sm={5}>
            <Box component={Hidden} xsDown>
              <img
                src={image}
                alt=" about us"
                className={classes.responsiveImg}
              />
            </Box>
          </Grid>
          <Grid item xs={12} sm={7}>
            {RenderSectionHeading({
              smallText: "Make a Comment!",
              heading: "Comment Section has been designed for you..",
              description: "We are always open for your feedbacks! ",
            })}
            <br />
            <form>
              <Grid
                className={classes.CommentForm}
                container
                direction="row"
                justify="center"
                alignItems="center"
              >
                <Grid item xs={12} sm={10}>
                  <TextField
                    margin="normal"
                    fullWidth
                    id="outlined-basic"
                    label="Your Name"
                    variant="outlined"
                    value={name}
                    onChange={handleChange}
                  />
                  <TextField
                    margin="normal"
                    fullWidth
                    id="outlined-basic"
                    label="Your Mail"
                    variant="outlined"
                    value={mail}
                    onChange={handleChange}
                  />
                  <TextField
                    margin="normal"
                    id="outlined-multiline-static"
                    label="Leave a Comment!"
                    multiline
                    rows={4}
                    fullWidth
                    comment={comment}
                    onChange={handleChange}
                  />
                  <Button fullWidth className={classes.submitBtn}>
                    Submit
                  </Button>
                  
                              </Grid>
                              <Button variant="outlined">
                    {name} ve {mail} asdae {comment}
                  </Button>
              </Grid>
            </form>
          </Grid>
        </Grid>
      </Container>
      {/* </ScrollAnimation> */}
    </Box>
  );
}
