import {
  Avatar,
  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Container,
  Grid,
  Hidden,
  TextField,
  Typography,
} from "@material-ui/core";
import React, { useState } from "react";
import { useStyles } from "./BodyStyle";
import image from "../../images/commentPic.jpeg";
import { RenderSectionHeading } from "../common/Common";

import { red } from "@material-ui/core/colors";

//import ScrollAnimation from 'react-animate-on-scroll';

export default function Comment() {
  const classes = useStyles();
  const [name, setName] = useState("");
  const [mail, setMail] = useState("");
  const [comment, setComment] = useState("");
  const [list, setList] = useState();
  const date = new Date();
  const handleChangeName = (event) => {
    setName(event.target.value);
  };
  const handleChangeMail = (event) => {
    setMail(event.target.value);
  };
  const handleChangeComment = (event) => {
    setComment(event.target.value);
  };
  var itemtoAdd = "";
  const handleSubmit = (e) => {
    e.preventDefault();
    itemtoAdd = e.target.firstChild.value;
    if (itemtoAdd !== "") {
      setList((state) => [...state, { id: len, todo: itemtoAdd }]);
    }
    e.target.firstChild.value = "";
  };

  return (
    <Box className={classes.section3} id="Comments">
      {/* <ScrollAnimation animateIn='fadeIn'> */}
      <Container>
        <Grid container spacing={1}>
          <Grid item sm={5}>
            <Box component={Hidden} xsDown>
              <img
                src={image}
                alt=" about us"
                className={classes.responsiveImg}
              />
            </Box>
          </Grid>
          <Grid item xs={12} sm={7}>
            {RenderSectionHeading({
              smallText: "Make a Comment!",
              heading: "Comment Section has been designed for you..",
              description: "We are always open for your feedbacks! ",
            })}
            <br />
            <form onSubmit={handleSubmit}>
              <Grid
                className={classes.CommentForm}
                container
                direction="row"
                justify="center"
                alignItems="center"
              >
                <Grid item xs={12} sm={10}>
                  <TextField
                    margin="normal"
                    fullWidth
                    id="outlined-basic"
                    label="Your Name"
                    variant="outlined"
                    value={name}
                    onChange={handleChangeName}
                  />
                  <TextField
                    margin="normal"
                    fullWidth
                    id="outlined-basic"
                    label="Your Mail"
                    variant="outlined"
                    value={mail}
                    onChange={handleChangeMail}
                  />
                  <TextField
                    margin="normal"
                    id="outlined-multiline-static"
                    label="Leave a Comment!"
                    multiline
                    rows={4}
                    fullWidth
                    value={comment}
                    onChange={handleChangeComment}
                  />
                  <Button fullWidth className={classes.submitBtn}>
                    Submit
                  </Button>
                  <Grid container spacing={2}>
                    {list.map((item) => (
                      <Grid item xs={12} md={6}>
                        <Card sx={{ maxWidth: 345 }}>
                          <CardHeader
                            avatar={
                              <Avatar
                                sx={{ bgcolor: red[500] }}
                                aria-label="recipe"
                              >
                                {name[0]}
                              </Avatar>
                            }
                            title={name}
                            subheader={date.getFullYear()}
                          />

                          <CardContent>
                            <Typography variant="body2" color="text.secondary">
                              {comment}
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Grid>
              </Grid>
            </form>
          </Grid>
        </Grid>
      </Container>
      {/* </ScrollAnimation> */}
    </Box>
  );
}
