import { Theme } from "../Theme";
import { Image } from "../../images/header-back.jpg";

export const useStyles = makeStyles(() => ({
  HeaderWrapper: {
    width: "100%",
    minHeight: "90vh",
    height:"auto",
    backgroundColor: `url(${Image})`
  },
  HeaderContainer: {
    
  },
  AppBar: {
    backgroundColor: Theme.colors.deepest
  },
  toolbar: {
     display: "flex",
    flexFlow: "row wrap",
