import {

  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Container,
  Grid,
  Hidden,
  TextField,
  Typography,
} from "@material-ui/core";
import React, { useState } from "react";
import { useStyles } from "./BodyStyle";
import image from "../../images/commentPic.jpeg";
import { RenderSectionHeading } from "../common/Common";
import Avatar from '@mui/material/Avatar';
import { red } from "@material-ui/core/colors";
import ListCard from "./ListCard";

//import ScrollAnimation from 'react-animate-on-scroll';

export default function Comment() {
  const classes = useStyles();
  const [name, setName] = useState("");
  const [mail, setMail] = useState("");
  const [comment, setComment] = useState("");
  const [list, setList] = useState([]);

  const date = new Date();
  const handleChangeName = (event) => {
    setName(event.target.value);
  };
  const handleChangeMail = (event) => {
    setMail(event.target.value);
  };
  const handleChangeComment = (event) => {
    setComment(event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setList([
      ...list,
      {
        id: Math.floor(Math.random() * 10000),
        title: name,
        title1: mail,
        title2: comment,
      },
    ]);
    setName("");
    setMail("");
    setComment("");
  };

  return (
    <Box className={classes.section3} id="Comments">
    
      <Container>
        <Grid container spacing={1}>
          <Grid item sm={5}>
            <Box component={Hidden} xsDown>
              <img
                src={image}
                alt=" about us"
                className={classes.responsiveImg}
              />
            </Box>
          </Grid>
          <Grid item xs={12} sm={7}>
            {RenderSectionHeading({
              smallText: "Make a Comment!",
              heading: "Comment Section has been designed for you..",
              description: "We are always open for your feedbacks! ",
            })}
            <br />
            <form onSubmit={handleSubmit}>
              <Box className={classes.formCommentBox} >
                <Grid
                  className={classes.CommentForm}
                  container
                  direction="row"
                  justify="center"
                  alignItems="center"
                >
                  <Grid item xs={12} sm={12}>
                    <TextField
                      margin="normal"
                      fullWidth
                      id="outlined-basic"
                      label="Your Name"
                      variant="outlined"
                      value={name}
                      onChange={handleChangeName}
                    />
                    <TextField
                      margin="normal"
                      fullWidth
                      id="outlined-basic"
                      label="Your Mail"
                      variant="outlined"
                      value={mail}
                      onChange={handleChangeMail}
                    />
                    <TextField
                      margin="normal"
                      id="outlined-multiline-static"
                      label="Leave a Comment!"
                      multiline
                      rows={4}
                      fullWidth
                      value={comment}
                      onChange={handleChangeComment}
                    />
                    <Button onClick={handleSubmit } fullWidth className={classes.submitBtnFrm}>
                      Submit
                    </Button>
                  </Grid>
                </Grid>
                <Grid container>
                  <Grid sx={{ display:"flex",  }} item xs={12} md={9}>
                    
                    
                    {list.map((item) => (
                      <div key={item.id}>
                        <Grid sx={{ml:2}} item xs={12} md={12}>
                <Card fullWidth sx={{ fullWidth:true }}>
                  <CardHeader
                    avatar={
                                <Avatar sx={{ color: "#885053" }} >
                        {item.title[0].toUpperCase()}
                      </Avatar>
                    }
                    title={item.title}
                    subheader={item.title1}
                  />

                  <CardContent>
                    <Typography variant="body2" color="text.secondary">
                      {item.title2}
                    </Typography>
                  </CardContent>
                </Card>
                </Grid>
                     </div>
                    )) }
                     {/* <ListCard list={list} setList={setList}
       name={name} setName={setName} 
       mail={mail} setMail={setMail} 
       comment={comment} setComment={setComment}/>  */}
                    
                    {/* {list.map((item) => (
                      <>
                        <li key={item.id}>{item.name}</li>
                      <li key={item.id}>{item.title1}</li>
                      <li key={item.id}>{item.title2}</li>
                      </>
                    ))} */}
                  </Grid>
                </Grid>
              </Box>
            </form>
          </Grid>
        </Grid>
      </Container>
   
    </Box>
  );
}
