import { makeStyles } from "@material-ui/core";
import {Theme} from "../Theme"

export const useStyles = makeStyles((theme) => ({
    boxAbout: {
        width: "100%",
        minHeight: "90vh",
        height: "auto",
        backgroundColor: Theme.colors.deep,
        padding: theme.spacing(10, 0, 8, 0),
         
    },
    responsiveImg: {
        width: "100%",
        height:"auto"
    },
    cardMedia: {
        color: Theme.colors.white,
        display: "flex",
        flexDirection: "column"
    },
    divider: {
        width: "64px",
        height: "4px",
        backgroundColor:"#0097a7"
    },
    sectionHeadingCont: {
        padding:theme.spacing(1)
    },
    sectionHeading: {
        margin:theme.spacing(1,0)
    },
    sectionDesc: {
        
    },
    AvatarCont: {
        display: "flex",
        flexFlow: "row",
        justifyContent: "center",
        alignItems:"center"
    },
    avatar: {
        backgroundColor:"#0097a7",
    },

    workBox: {
        width: "100%",
        minHeight: "90vh",
        height: "auto",
        backgroundColor: Theme.colors.deepest,
        padding: theme.spacing(10, 0, 8, 0),
    },
    imageBox: {
        position: "relative",
        "&:hover $overlayImg": {
      opacity: 1,
    },
    },
    overlayImg: {
        position: "absolute",
    width: "100%",
    height: "100%",
    top: 0,
    left: 0,
    backgroundColor: "#0097a7",
    color: Theme.colors.white,
    display: "flex",
    flexFlow: "column wrap",
    justifyContent: "center",
    alignItems: "center",
    opacity: 0,
    transition: "0.7s",
        
    },
    overlayTitle: {
    fontSize: "2rem",
    marginBottom: "16px",
    [theme.breakpoints.down("xs")]: {
      fontSize: "1.2rem",
    },
    },
    respImg: {
        
    width: "100%",
    height: "auto",
    },
    submitBtn: {
        backgroundColor: "#0097a7",
        color: Theme.colors.white,
        "&:hover": {
            backgroundColor:Theme.colors.deep,
        },
        
    },
    formContainer: {
    padding: theme.spacing(2),
    margin: theme.spacing(2, 0),
    },
    footerBox: {
        
    }
    
}))
